// Initialize WhatsApp connection check
async function checkWhatsAppConnection() {
  try {
    // Set loading state
    variables['9d09d623-d74f-46b4-bf0d-7e6858183e5a'] = {
      status: 'loading',
      qrcode: null
    };

    // Get current user ID (you may need to adjust this based on your auth system)
    const userId = globalContext.user?.id || 'default_user';

    // Make POST request to Evolution API
    const response = await fetch('https://webhook.exittodigital.com.br/webhook/get-qrcode', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        client_id: userId
      })
    });

    if (response.ok) {
      const data = await response.json();

      // Update whatsappConnection variable with response data
      variables['9d09d623-d74f-46b4-bf0d-7e6858183e5a'] = {
        status: data.status || 'waiting',
        qrcode: data.qrcode || null
      };

      // If not connected, poll again after 15 seconds
      if (data.status !== 'open') {
        setTimeout(() => {
          checkWhatsAppConnection();
        }, 15000);
      }
    } else {
      // Handle error - set error state
      variables['9d09d623-d74f-46b4-bf0d-7e6858183e5a'] = {
        status: 'error',
        qrcode: null
      };

      // Retry after 15 seconds on error
      setTimeout(() => {
        checkWhatsAppConnection();
      }, 15000);
    }
  } catch (error) {
    console.error('WhatsApp connection check failed:', error);

    // Set error state
    variables['9d09d623-d74f-46b4-bf0d-7e6858183e5a'] = {
      status: 'error',
      qrcode: null
    };

    // Retry after 15 seconds on error
    setTimeout(() => {
      checkWhatsAppConnection();
    }, 15000);
  }
}

// Start the connection check
checkWhatsAppConnection();

